import { useToggle } from "../../hooks/useToggle";
import navLinks from "./navLinks";
import React from "react";
import Box from "@chakra-ui/core/dist/Box";
import { color } from "../../customTheme";
import { useColorMode } from "@chakra-ui/core";

const Nav = () => {
  const { isToggled, toggle } = useToggle();
  const { colorMode, toggleColorMode } = useColorMode();

  return (
    <div>
      <Box color={color[colorMode]} className="nav-container">
        <ul className="nav-list-container">{navLinks()}</ul>
        <button onClick={toggle} className="menu-button">
          меню
        </button>
      </Box>
      {isToggled && <ul className="nav-list-container-mobile">{navLinks()}</ul>}
    </div>
  );
};

export default Nav;
