import React, { useEffect } from "react";
import Layout from "../components/layout/Layout";
import { getAllPosts } from "../lib/api";
import { useToggle } from "../hooks/useToggle";
import IconsBtn from "../components/IconsBtn";
import { gridIcon, listIcon } from "../lib/icons";
import PostCard from "../components/posts-preview/PostCard";
import { useWindowWidth } from "../hooks/useWindowWidth";

const App = ({ posts = [] }) => {
  const { isToggled, setToggle, toggle } = useToggle();

  const width = useWindowWidth();

  useEffect(() => {
    width <= 768 && setToggle(false);
  }, [width]);

  const previews = posts.map(
    ({ _id, title, subtitle, slug, mainImage, publishedAt, body }) => (
      <PostCard
        key={_id}
        title={title}
        subtitle={subtitle}
        slug={slug}
        mainImage={mainImage}
        publishedAt={publishedAt}
        body={body}
        isToggled={isToggled}
      />
    )
  );

  const gridClass = isToggled
    ? "md:mx-16 lg:mx-16 xl:mx-16 ip:mx-64"
    : "lg:grid-cols-2 lg:mt-2 lg:mx-16 ip:mx-64 xl:mx-16 ip:grid-cols-3 xl:gap-12";

  return (
    <Layout>
      <div className="hidden h-10 mt-6 justify-start items-center lg:flex md:px-16 xl:px-16 ip:px-64">
        <IconsBtn
          firstIcon={gridIcon}
          secondIcon={listIcon}
          isToggled={isToggled}
          toggle={toggle}
        />
      </div>
      <div className={`mb-6 grid grid-cols-1 gap-6 ${gridClass}`}>
        {previews}
      </div>
    </Layout>
  );
};

export async function getStaticProps() {
  const posts = await getAllPosts();
  return {
    props: {
      posts,
    },
  };
}

export default App;
