import client from "./client";
import groq from "groq";

export const getAllPosts = async () => {
  const fields = `
  title,
  subtitle,
  slug: slug.current
  `;
  return await client.fetch(groq`
      *[_type == "post"]{${fields}|order(publishedAt desc)
    `);
};
