import Link from "next/link";
import Hamburger from "./Hamburger";
import Drawer from "./Drawer";

const pages = {
  blog: "блог",
  about: "об этом блоге",
  statement: "утверждение веры",
};

const links = Object.keys(pages).map((link) => {
  return (
    <li className="pb-6 lg:pl-4 " key={link}>
      <Link href={link === "blog" ? "/" : `/${link}`}>
        <a>{pages[link]}</a>
      </Link>
    </li>
  );
});

const Nav = () => {
  return (
    <nav className="uppercase tracking-wide font-menu text-gray-700">
      <ul className="hidden lg:flex text-xl">{links}</ul>
      <Hamburger />
      <Drawer links={links} />
    </nav>
  );
};

export default Nav;
