import React, { useState } from "react";
import Input from "@chakra-ui/core/dist/Input";
import { IconButton, InputGroup, InputRightElement } from "@chakra-ui/core";
import { FiSearch } from "react-icons/fi";
import Box from "@chakra-ui/core/dist/Box";
import { motion } from "framer-motion";

const SearchInput = ({ switchToSearchInput }) => {
  const [value, setValue] = useState("");

  const handleChange = (e) => setValue(e.target.value);

  const MotionInput = motion.custom(Input);

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      console.log(value);
      switchToSearchInput();
    }
  };

  const handleClick = () => {
    console.log(value);
    switchToSearchInput();
  };

  return (
    <InputGroup>
      <MotionInput
        initial={{ x: 0, opacity: 0 }}
        animate={{ x: 100, opacity: 1 }}
        transition={{
          delay: 1,
          x: { type: "spring", stiffness: 100 },
        }}
        value={value}
        autoFocus
        onBlur={switchToSearchInput}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        placeholder="поиск"
        borderColor="gray.300"
        width={{ base: "250px", lg: "300px" }}
        variant="outline"
        rounded="full"
        _focus={{ outline: "none" }}
      />
      <InputRightElement
        rounded="full"
        bg="gray.800"
        cursor="pointer"
        _hover={{ bg: "white", borderColor: "gray.50" }}
        children={
          <Box
            onClick={handleClick}
            as={FiSearch}
            color="white"
            size={{ base: "20px", lg: "30px" }}
          />
        }
      />
    </InputGroup>
  );
};

export default SearchInput;
