import React from "react";
import BlockContent from "@sanity/block-content-to-react";
import AuthorDate from "../shared/AuthorDate";
import Box from "@chakra-ui/core/dist/Box";
import Heading from "@chakra-ui/core/dist/Heading";
import { useColorMode } from "@chakra-ui/core";
import {
  bgColor,
  bgColorReversed,
  borderColor,
  color,
  colorReversed,
} from "../../customTheme";

const PostBody = ({ body, title, subtitle, publishedAt, name }) => {
  const { colorMode } = useColorMode();

  return (
    <Box className="article-body-container">
      <AuthorDate publishedAt={publishedAt} secondProp={name} />
      <Heading
        as="h1"
        fontSize="4xl"
        color={color[colorMode]}
        className="article-body-title"
      >
        {title}
      </Heading>
      <Heading as="h2" fontSize="2xl" className="article-body-subtitle">
        {subtitle}
      </Heading>
      <BlockContent className="my-6" blocks={body} />
    </Box>
  );
};

export default PostBody;
