import React, { useContext } from "react";
import { urlFor } from "../../lib/helpers";
import { GlobalContext } from "../../context/global.context";
import { AspectRatioBox } from "@chakra-ui/core";
import Image from "next/image";
import {IAllPosts} from '../../interfaces/IAllPosts';

const PostCover = ({ mainImage, title }: IAllPosts) => {
  const { isListView } = useContext(GlobalContext);
  const image = mainImage.replace("https://", '');

  const coverImageContainer = isListView && "w-1/2";

  return (
    <AspectRatioBox
      ratio={16 / 9}
      className={`${coverImageContainer} coverImage-container`}
    >
      <Image
        width="100%"
        height="100%"
        className="coverImage"
        src={image}
        alt={title}
      />
    </AspectRatioBox>
  );
};

export default PostCover;
