import React, { useContext } from "react";
import { urlFor } from "../../lib/helpers";
import { GlobalContext } from "../../context/global.context";
import { AspectRatioBox } from "@chakra-ui/core";

const PostCover = ({ mainImage, title }) => {
  const { isListView } = useContext(GlobalContext);

  const coverImageContainer = isListView
    ? "w-1/2 relative"
    : "coverImage-container";

  return (
    <AspectRatioBox ratio={16 / 9} className={coverImageContainer}>
      <img
        className="w-full h-full block object-cover top-0 left-0 align-top absolute"
        src={urlFor(mainImage).auto("format").url()}
        alt={title}
      />
    </AspectRatioBox>
  );
};

export default PostCover;
