import React from "react";
import Nav from "components/layout/Nav";
import { moonIcon, searchIcon, sunIcon } from "../../lib/icons";
import IconsBtn from "../shared/IconsBtn";
import { MAIN_TITLE } from "../../lib/vars";
import { useToggle } from "../../hooks/useToggle";
import Input from "@chakra-ui/core/dist/Input";
import Icon from "@chakra-ui/core/dist/Icon";
import InputGroup from "@chakra-ui/core/dist/InputGroup";
import { InputRightElement } from "@chakra-ui/core";
import SearchInput from "./SearchInput";

const Header = () => {
  const { isToggled: isThemeChanged, toggle: changeTheme } = useToggle();
  const { isToggled: isSearchInput, toggle: switchToSearchInput } = useToggle();

  const headerTitleClass = isSearchInput && "";

  return (
    <>
      <div className="header-container">
        <IconsBtn
          firstIcon={sunIcon}
          secondIcon={moonIcon}
          toggle={changeTheme}
          isToggled={isThemeChanged}
          className="ease"
        />
        <h1 className={`header-title justify-self-center ${headerTitleClass}`}>
          {MAIN_TITLE}
        </h1>

        {isSearchInput ? (
          <SearchInput />
        ) : (
          <button onClick={switchToSearchInput}>{searchIcon}</button>
        )}
      </div>
      <Nav />
    </>
  );
};

export default Header;
