import React, { useContext } from "react";
import PostCard from "./PostCard";
import { getAllPosts } from "../../lib/api";
import { useGetPosts } from "../../actions";
import { PAGE_SIZE } from "../../lib/vars";
import IconsBtn from "../shared/IconsBtn";
import { BsGrid } from "react-icons/bs";
import { FiList } from "react-icons/fi";
import { Skeleton } from "@chakra-ui/core";
import Box from "@chakra-ui/core/dist/Box";
import { borderColor, color } from "../../customTheme";
import Button from "@chakra-ui/core/dist/Button";
import { downChevron } from "../../lib/icons";
import { GlobalContext } from "../../context/global.context";

export async function getStaticProps() {
  const posts = await getAllPosts(0);
  return {
    props: {
      posts,
    },
  };
}

const Previews = ({ posts }) => {
  const { setListView, isListView, switchView } = useContext(GlobalContext);

  const initialData = posts && [posts];

  const { data: paginatedPosts, size, setSize } = useGetPosts(initialData);

  const isEmpty = paginatedPosts?.[0]?.length === 0;
  const isReachingEnd =
    isEmpty ||
    (paginatedPosts &&
      paginatedPosts[paginatedPosts.length - 1]?.length < PAGE_SIZE);

  if (!paginatedPosts) return "loading";

  const previews =
    paginatedPosts &&
    paginatedPosts.map(
      ({ _id, title, subtitle, slug, mainImage, publishedAt, body }) => (
        <PostCard
          key={_id}
          title={title}
          subtitle={subtitle}
          slug={slug}
          mainImage={mainImage}
          publishedAt={publishedAt}
          body={body}
        />
      )
    );

  return (
    <Box>
      <div className="view-button-container">
        <IconsBtn
          whileHover={{ scale: 1.2 }}
          firstIcon={BsGrid}
          secondIcon={FiList}
          toggle={switchView}
          isToggled={isListView}
          className="ease"
        />
      </div>
      <Skeleton isLoaded={paginatedPosts}>
        <Box
          color={color[colorMode]}
          className={`preview-container ${previewClass}`}
        >
          {previews}
        </Box>
      </Skeleton>
      <div className="preview-button-container">
        <Button
          disabled={isReachingEnd}
          size="lg"
          rounded="full"
          onClick={() => !isReachingEnd && setSize(size + 1)}
          variant="outline"
          borderColor={borderColor[colorMode]}
          _focus={{ outline: "none" }}
        >
          {downChevron(isReachingEnd)}
        </Button>
      </div>
    </Box>
  );
};

export default Previews;
