import React, { useEffect, useState } from "react";
import Layout from "../components/layout/Layout";
import PostGridCard from "../components/posts-preview/PostGridCard";

import { getAllPosts } from "../lib/api";
import { useToggle } from "../hooks/useToggle";

import IconsBtn from "../components/IconsBtn";
import { gridIcon, listIcon } from "../lib/icons";
import PostListCard from "../components/posts-preview/PostListCard";

const App = ({ posts = [] }) => {
  const { isToggled, setToggle, toggle } = useToggle();
  const [width, setWidth] = useState(0);

  useEffect(() => {
    setWidth(window.innerWidth);
    width <= 1024 && setToggle(false);
    console.log(width);
  }, [window.innerWidth]);

  const gridClass = isToggled
    ? "xl:mx-64"
    : "lg:grid-cols-2 lg:mt-2 lg:mx-16 xl:mx-64 xl:grid-cols-3 xl:gap-12";

  const previews = posts.map(
    ({ _id, title, subtitle, slug, mainImage, publishedAt, body }) =>
      !isToggled ? (
        <PostGridCard
          key={_id}
          title={title}
          subtitle={subtitle}
          slug={slug}
          mainImage={mainImage}
          publishedAt={publishedAt}
        />
      ) : (
        <PostListCard
          key={_id}
          title={title}
          subtitle={subtitle}
          slug={slug}
          mainImage={mainImage}
          publishedAt={publishedAt}
          body={body}
        />
      )
  );

  return (
    <Layout>
      <div className="hidden h-10 lg:px-16 xl:px-64 mt-6 lg:flex justify-start items-center">
        <IconsBtn
          firstIcon={gridIcon}
          secondIcon={listIcon}
          isToggled={isToggled}
          toggle={toggle}
        />
      </div>
      <div className={`mb-6 grid grid-cols-1 gap-6 ${gridClass}`}>
        {previews}
      </div>
    </Layout>
  );
};

export async function getStaticProps() {
  const posts = await getAllPosts();
  return {
    props: {
      posts,
    },
  };
}

export default App;
