import React from "react";
import BlockContent from "@sanity/block-content-to-react";
import Layout from "components/layout/Layout";
import { urlFor } from "lib/helpers";
import { getSinglePost, getAllPosts } from "lib/api";
import client from "../../lib/client";
import PostCover from "../../components/PostCover";
import { format } from "date-fns";
import { ru } from "date-fns/locale";
import PublishedDate from "../../components/PublishedDate";

const Post = ({
  post: { mainImage, title, subtitle, publishedAt, name, body },
}) => {
  console.log(subtitle);
  return (
    <Layout>
      <article className="h-full mt-6 mb-20 text-gray-800">
        {mainImage && <PostCover mainImage={mainImage} title={title} />}
        <div className="max-w-4xl  mx-auto">
          <h1 className="font-bold text-2xl my-4">{title}</h1>
          <h2 className="font-semibold text-gray-800">{subtitle}</h2>

          <BlockContent className="my-4" blocks={body} {...client.config()} />
          {/*{post.categories && (*/}
          {/*  <ul>*/}
          {/*    {post.categories.map((category) => (*/}
          {/*      <li key={category}>{category}</li>*/}
          {/*    ))}*/}
          {/*  </ul>*/}
          {/*)}*/}
        </div>
        <div className="py-2 flex justify-between text-sm text-gray-600">
          <span className="hover:text-gray-800">{name}</span>
          <PublishedDate publishedAt={publishedAt} />
        </div>
      </article>
    </Layout>
  );
};

export async function getStaticProps({ params }) {
  const post = await getSinglePost(params.slug);
  return {
    props: {
      post,
    },
  };
}

export async function getStaticPaths() {
  const posts = await getAllPosts();

  return {
    paths: posts.map((post) => ({
      params: { slug: post.slug.current },
    })),
    fallback: false,
  };
}

export default Post;
