import React from "react";
import Layout from "../components/layout/Layout";
import PostGridCard from "../components/posts-preview/PostGridCard";
import { Tooltip } from "@chakra-ui/core";
import { getAllPosts } from "../lib/api";
import { useToggle } from "../hooks/useToggle";
import { motion } from "framer-motion";
import IconsBtn from "../components/IconsBtn";
import { gridIcon, listIcon } from "../lib/icons";

const App = ({ posts = [] }) => {
  const { isToggled, toggle } = useToggle();

  const previews = posts.map(
    ({
      _id,
      title = "",
      subtitle = "",
      slug = "",
      mainImage,
      publishedAt = "",
    }) => (
      <PostGridCard
        key={_id}
        title={title}
        subtitle={subtitle}
        slug={slug}
        mainImage={mainImage}
        publishedAt={publishedAt}
      />
    )
  );

  return (
    <Layout>
      <div className="hidden h-10 lg:px-16 xl:px-48 mt-6 lg:flex justify-start items-center">
        <IconsBtn firstIcon={gridIcon} secondIcon={listIcon} />
      </div>
      <div className="mb-6 grid grid-cols-1 gap-6 lg:grid-cols-2 lg:mt-2 lg:mx-16 xl:mx-48 xl:grid-cols-3 xl:gap-12">
        {previews}
      </div>
    </Layout>
  );
};

export async function getStaticProps() {
  const posts = await getAllPosts();
  return {
    props: {
      posts,
    },
  };
}

export default App;
