import React from "react";
import Layout from "../components/layout/Layout";
import PostCard from "../components/PostCard";

import { getAllPosts } from "../lib/api";

const App = ({ posts = [] }) => {
  const previews = posts.map(
    ({
      _id,
      title = "",
      subtitle = "",
      slug = "",
      mainImage,
      publishedAt = "",
    }) => (
      <PostCard
        key={_id}
        title={title}
        subtitle={subtitle}
        slug={slug}
        mainImage={mainImage}
        publishedAt={publishedAt}
      />
    )
  );

  return (
    <Layout>
      <div className="my-6 grid-cols-1 gap-1 lg:mx-32 grid lg:grid-cols-3 lg:gap-12">
        {previews}
      </div>
    </Layout>
  );
};

export async function getStaticProps() {
  const posts = await getAllPosts();
  return {
    props: {
      posts,
    },
  };
}

export default App;
