import React, { forwardRef } from "react";
import { urlFor } from "../lib/helpers";

const PostCard = forwardRef(({ slug, _id, mainImage, title, href }, ref) => {
  return (
    slug && (
      <a href={href}>
        <div className="rounded shadow overflow-hidden" key={_id}>
          {mainImage && (
            <div>
              <img
                className="w-full h-full block object-cover top-0 left-0 align-top"
                src={urlFor(mainImage).auto("format").url()}
                alt={title}
              />
            </div>
          )}
          <div className="p-4">{title}</div>
        </div>
      </a>
    )
  );
});

export default PostCard;
