import Link from "next/link";
import Hamburger from "./Hamburger";

const pages = {
  blog: "блог",
  about: "об этом блоге",
  statement: "утверждение веры",
};

const links = Object.keys(pages).map((link) => {
  return (
    <li className="pb-6 lg:pl-4 " key={link}>
      <Link href={link === "blog" ? "/" : `/${link}`}>
        <a>{pages[link]}</a>
      </Link>
    </li>
  );
});

const Nav = () => {
  return (
    <nav className="uppercase tracking-wide font-menu text-gray-700 text-xl">
      <ul className="hidden lg:flex">{links}</ul>
      <Hamburger />
      <ul className="h-full w-full flex flex-col justify-center items-center lg:hidden">
        {links}
      </ul>
    </nav>
  );
};

export default Nav;
