import React from "react";
import { urlFor } from "../lib/helpers";
import Link from "next/link";

const PostCard = ({ slug, _id, _updatedAt, mainImage, title }) => {
  return (
    slug && (
      <Link href="/post/[slug]" as={`/post/${slug.current}`}>
        <a>
          <div className="rounded shadow overflow-hidden">
            {mainImage && (
              <div>
                <img
                  className="w-full h-full block object-cover top-0 left-0 align-top"
                  src={urlFor(mainImage).auto("format").url()}
                  alt={title}
                />
              </div>
            )}
            <h1 className="p-4 font-bold text-lx text-gray-800">{title}</h1>
            {new Date(_updatedAt).toDateString()}
          </div>
        </a>
      </Link>
    )
  );
};

export default PostCard;
