import React, { useEffect } from "react";
import Link from "next/link";
import { motion } from "framer-motion";
import PostAuthorDate from "../single-post/PostAuthorDate";
import { urlFor } from "../../lib/helpers";
import BlockContent from "@sanity/block-content-to-react";

const PostListCard = ({
  slug,
  mainImage,
  subtitle,
  publishedAt,
  title,
  body,
  isToggled,
}) => {
  const variants = {
    hover: {
      scale: 1.03,
      boxShadow: "0 5px 10px rgba(0,0,0,.5)",
      transition: { ease: "easeOut" },
    },
    off: { scale: 1 },
  };

  useEffect(() => {
    console.log(isToggled);
  }, [isToggled]);

  return (
    slug && (
      <Link href="/post/[slug]" as={`/post/${slug.current}`}>
        <motion.a
          whileHover="hover"
          variants={variants}
          className="mt-6 border border-gray-400 rounded-lg overflow-hidden flex"
        >
          {mainImage && (
            <img
              src={urlFor(mainImage).auto("format").url()}
              alt={title}
              className="w-1/3"
            />
          )}
          <div className=" w-full p-4 lg:p-6 flex flex-col justify-between">
            <div>
              <h1 className="font-bold text-gray-800 text-2xl">{title}</h1>
              <h2 className="font-semibold text-xl text-gray-800 my-6">
                {subtitle}
              </h2>
              {!isToggled && (
                <BlockContent
                  className="hidden lg:block my-4 text-lg"
                  blocks={body}
                />
              )}
            </div>
            <PostAuthorDate secondProp={""} publishedAt={publishedAt} />
          </div>
        </motion.a>
      </Link>
    )
  );
};

export default PostListCard;
