import React from "react";
import { urlFor } from "../lib/helpers";
import Link from "next/link";

const PostCard = ({ slug, _id, _updatedAt, mainImage, title }) => {
  return (
    slug && (
      <Link href="/post/[slug]" as={`/post/${slug.current}`}>
        <a>
          <div className="rounded shadow overflow-hidden h-64">
            {mainImage && (
              <div>
                <img
                  className="w-full h-full block object-cover top-0 left-0 align-top"
                  src={urlFor(mainImage).auto("format").url()}
                  alt={title}
                />
              </div>
            )}
            <div className="p-4">
              <h1 className="font-bold text-gray-800">{title}</h1>
              <p>{new Date(_updatedAt).toDateString()}</p>
            </div>
          </div>
        </a>
      </Link>
    )
  );
};

export default PostCard;
