import client from "../../client";
import BlockContent from "@sanity/block-content-to-react";
import groq from "groq";
import Layout from "../../components/layout/Layout";
import { urlFor } from "../../lib/helpers";

const Post = ({ title = "", name = "", categories, body = [], mainImage }) => {
  return (
    <Layout>
      <article className="h-full mb-20">
        {mainImage && (
          <div className="mainImage-container mt-20">
            <img
              className="w-full h-full block object-cover top-0 left-0 align-top"
              src={urlFor(mainImage).auto("format").url()}
            />
          </div>
        )}
        <div className="max-w-4xl p-8 mx-auto">
          <h1>{title}</h1>
          <span>By {name}</span>
          {categories && (
            <ul>
              Posted in
              {categories.map((category) => (
                <li key={category}>{category}</li>
              ))}
            </ul>
          )}
          <BlockContent
            blocks={body}
            imageOptions={{ w: 320, h: 240, fit: "max" }}
            {...client.config()}
          />
        </div>
      </article>
    </Layout>
  );
};

const query = groq`*[_type == "post" && slug.current == $slug][0]{
  title,
  "name": author->name,
  "categories": categories[]->title,
  mainImage,
  body
}`;

Post.getInitialProps = async function (context) {
  const { slug = "" } = context.query;
  return await client.fetch(query, { slug });
};

export default Post;
