import Grid from "@chakra-ui/core/dist/Grid";
import { useColorMode } from "@chakra-ui/core";
import About from "./About";

const Aside = () => {
  const { colorMode } = useColorMode();

  return (
    <Box as="aside" mt="4">
      <About />
      <Divider borderColor={borderColor[colorMode]} />
    </Box>
  );
};

export default Aside;
