import React, { createContext } from "react";
import Link from "next/link";
import { useToggle } from "../hooks/useToggle";

export const GlobalContext = createContext(undefined, undefined);

export const GlobalProvider = ({ children }) => {
  const {
    isToggled: isOpened,
    setIsToggled: setIsOpened,
    toggle: open,
  } = useToggle();

  const pages = {
    blog: "блог",
    about: "об этом блоге",
    statement: "утверждение веры",
    give: "поддержи проект",
  };

  const links = Object.keys(pages).map((link) => {
    return (
      <li className="pb-12 lg:pl-4 " key={link}>
        <Link href={link === "blog" ? "/" : `/${link}`}>
          <a>{pages[link]}</a>
        </Link>
      </li>
    );
  });

  return (
    <GlobalContext.Provider value={{ links, isOpened, setIsOpened, open }}>
      {children}
    </GlobalContext.Provider>
  );
};
